import './App.css';
import { useState } from 'react'
import { Button } from '@material-ui/core'

const App = () => {
  const [todos, setTodos] = useState(['guilherme', 'felipe'])
  const [input, setInput] = useState('')

  const addTodo = (e) => {
    setTodos([...todos, input])
    setInput('')
    e.preventDefault() // para o refresh causado pelo Submit
  }

  return (
    <div className="App">      
      <form>
        <h1>Hello App</h1>
          <input value={input} onChange={e => setInput(e.target.value)}/>
            <Button type="submit" onClick={addTodo} variant="contained" color="primary">
              Add Todo
            </Button>
              <ul>
              {todos.map(todo => ( // mapeando o state todos para imprimir na li
                  <li>{todo}</li>))}
              </ul>
      </form>
    </div>
  );
}

export default App;
